mapper.select:
select user_no, user_id, user_name, user_password, user_phone
from user

    
        where ${key} = #{word}
    
    
        where ${key} like concat('%', #{word}, '%')
    

order by user_no
limit #{start}, #{spp}

select count(user_no) from user
where ${key} = #{word}
where ${key} like concat('%', #{word}, '%')

mapper.update:
update user
set
	
		user_id = #{user_id},
	
	
		user_name = #{user_name},
	
	
		user_password = #{user_password},
	
	
		user_phone = #{user_phone}
	
	
		user_no = #{user_no}
	
where user_no = #{user_no}
