mapper.select:
select * from marker where longitude != '0'
and (ccba_mnm like concat('%', #{word}, '%') or ccba_ctcd_nm like concat('%', #{word}, '%') or ccsi_name like concat('%', #{word}, '%'))
order by rand() limit 1000;

select *
from heritage
where ccba_asno = #{asno}
and ccba_ctcd = #{ctcd}
and ccba_kdcd = #{kdcd}
order by rand() limit 1000;
